mapper.delete:
    delete t1
      from subject_attribute t1
inner join attribute t2 on t1.attribute_id = t2.id
     where t1.subject_id = #{subjectId}
       and t2.type = #{attributeType}

mapper.select:
    select t1.*
      from subject_attribute t1
inner join attribute t2 on t1.attribute_id = t2.id
     where t1.subject_id = #{subjectId}
       and t2.type = #{attributeType}

    select t1.*
      from subject_attribute t1
inner join attribute t2 on t1.attribute_id = t2.id
     where t2.value = #{attributeValue}
       and t2.type = #{attributeType}
